     return 'no';
  }

// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
//
// For example,
//
  answer = ifScript();

  function ifScript() {
     if (workflow.scratchpad.responseError == true) {
        return 'yes';
     }
     return 'no';
  }

var x = new Nous_GetAPIKey();
var apiKey = x.getValidAPIKey();
var mid_server = gs.getProperty('x_nouin_nousvenafi.midserver');


var certificateDN = current.certificate_dn;
var payload = {"CertificateDN": String(certificateDN)};
	var json = JSON.stringify(payload);
	gs.info("Revoke json in WF:"+json);
	gs.info("CertificateDN in WF:"+certificateDN);
	var restMessage = new sn_ws.RESTMessageV2(); //Rest Call to Venafi
	restMessage.setHttpMethod("POST");
	restMessage.setEndpoint("https://10.0.0.126/VEDSDK/Certificates/Revoke");
	restMessage.setRequestHeader('Content-Type','application/json');
	restMessage.setRequestHeader('X-Venafi-Api-Key', apiKey);
	restMessage.setRequestBody(json);
	restMessage.setMIDServer(mid_server);
	var response = restMessage.execute();
	response.waitForResponse(100);
	var responseBody = response.getBody();
	gs.info("Certificate Revoked Response from WF:"+responseBody);
	var objJSON = JSON.parse(responseBody);
	var revoke = objJSON.Revoked;
	var requested = objJSON.Requested;
	var success = objJSON.Success;
	workflow.scratchpad.responseError=false;
	gs.info("Certificate Revoked Response in WF:"+revoke);
	gs.info("Certificate Revoke requested Response in WF:"+requested);
	gs.info("Certificate success Responsein WF:"+success);
	
	if(requested == true){
		workflow.scratchpad.responseError=true;
		current.status = "Pending Revocation";
// 		gs.info("Certificate Revoked Response inside WF if:"+current.status);
// 		current.status = "Revoked";
		current.update();
		
	}
	else if(revoke == true){
	gs.info("Certificate Revoked Response inside WF else:"+current.status);
		current.status = "Revoked";
		current.update();
	}

