var mid_server = gs.getProperty('x_nouin_nousvenafi.midserver');
var venafi_url = gs.getProperty('x_nouin_nousvenafi.venafi_url');
//gs.info("MID Server :"+mid_server);

var payload = {"ObjectDN": String(workflow.scratchpad.certificateDN)};
	var json = JSON.stringify(payload);
	//gs.info("DNtoGUID Request Body:"+ json);
//gs.info("workflow.scratchpad.certificateDN:"+ workflow.scratchpad.certificateDN);
try{
	var rm = new sn_ws.RESTMessageV2();
	rm.setEndpoint(venafi_url+"/Config/DnToGuid");
	rm.setHttpMethod("POST");
	rm.setRequestHeader('X-Venafi-Api-Key', workflow.scratchpad.apiKey);
	rm.setRequestHeader('Content-Type','application/json');
	rm.setMIDServer(mid_server);
	rm.setRequestBody(json);
	var response = rm.execute();
	var responseBody = response.getBody();
	//gs.info("DNtoGUID Response:"+responseBody);
	//Parse the output
	var objJSON1 = JSON.parse(responseBody);
	var id = objJSON1.GUID;
	gs.info("GUID1"+id);
	var GUID = id.replace(/{/g,"").replace(/}/g,"");
		var GUID1 = GUID.toString();
		activity.output = GUID1;
		workflow.scratchpad.guid = activity.output;
		//gs.info('GUID in DNtoGUID:'+workflow.scratchpad.guid);
		
		//Setting CertificateDN & GUID value in Certificate Request Table
		var gr = new GlideRecord('x_nouin_nousvenafi_certificate_rqst');
		gr.addQuery('requested_item',current.sys_id);
		gr.query();
		while(gr.next()){
			gr.certificate_dn = workflow.scratchpad.certificateDN;
			gr.guid = workflow.scratchpad.guid;
			gr.update();
		}

	
var responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
var	status = response.getStatusCode();
}
catch(ex) {
	responseBody = ex.getMessage();
	status = '500';
} finally {
	requestBody = rm ? rm.getRequestBody():null;
}

