	requestBody = sm ? sm.getRequestBody():null;
}

// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the approval status for this approval.  When called, the following information is available:
//
//    counts.total = total number of users that are part of this approval
//    counts.approved = # of users that approved so far
//    counts.rejected = # of users that rejected so far
//    counts.requested = # of users that are pending approval
//    counts.not_requested = # of users that are not pending approval
//    counts.not_required = # of users that approval is not required
//

// Set the variable 'answer' to a comma-separated list of user ids and/or group ids or an array of user/group ids to add as approvers.
//
// For example:
//       answer = [];
//       answer.push('id1');
//       answer.push('id2');

// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the approval status for this approval.  When called, the following information is available:
//
//    counts.total = total number of users that are part of this approval
//    counts.approved = # of users that approved so far
//    counts.rejected = # of users that rejected so far
//    counts.requested = # of users that are pending approval
//    counts.not_requested = # of users that are not pending approval
//    counts.not_required = # of users that approval is not required
//

// Set the variable 'answer' to a comma-separated list of user ids and/or group ids or an array of user/group ids to add as approvers.
//
// For example:
//       answer = [];
//       answer.push('id1');
//       answer.push('id2');

//gs.eventQueue("x_nouin_nousvenafi.request.certificate.det",current,workflow.scratchpad.guid,workflow.scratchpad.apiKey);


var url = gs.getProperty('x_nouin_nousvenafi.venafi_url');
var mid_server = gs.getProperty('x_nouin_nousvenafi.midserver');
var urlNew = url+'certificates'+'/'+workflow.scratchpad.guid;
//var urlNew = url+'certificates'+'/'+'152c0a4f-8d8c-4fb6-9c86-17afaa95f703';
var responseBody;
var validTo;
var validFrom;
var certauth;

try{

var r = new sn_ws.RESTMessageV2();
r.setEndpoint(urlNew);
r.setHttpMethod("GET");
r.setRequestHeader('Content-Type','application/json');
r.setRequestHeader('X-Venafi-Api-Key', workflow.scratchpad.apiKey);//workflow.scratchpad
r.setMIDServer(mid_server);
var response = r.execute();
response.waitForResponse(2000);
responseBody = response.getBody();
gs.info("Certificate Details Response+get:"+responseBody);

var objJSON = JSON.parse(responseBody);
	validTo = objJSON.CertificateDetails.ValidTo;
	validFrom = objJSON.CertificateDetails.ValidFrom;
	certauth = objJSON.CertificateAuthorityDN;
	workflow.scratchpad.responseError=false;
// 	gs.info("Certificate Details ValidTo"+validTo);

if (validTo == undefined && validTo =="NULL") {
	workflow.scratchpad.responseError = true;
	//gs.info("Response is undefined and not proper,Please try again to download the Certificate details"+validTo);
}

else{
		var gr = new GlideRecord('x_nouin_nousvenafi_certificate_rqst');
		gr.addQuery('requested_item',current.sys_id);
	    gs.info(current.sys_id);
		gr.query();
		while(gr.next()){
		gr.certificate_valid_to = validTo;
		gr.certificate_valid_from = validFrom;
		gr.certificate_authority = certauth;
		gs.info("Certificate Autho:" +certauth);	
		gr.api_key=	workflow.scratchpad.apiKey;
		gr.update();
		}
}
var responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
var	status = response.getStatusCode();
	gs.info("status:"+status);
} catch(ex) {
	responseBody = ex.getMessage();
	status = '500';
} finally {
	requestBody = r ? r.getRequestBody():null;
}

